// Scrolltotop.js
import React, { useEffect } from "react";
import { useLocation } from "react-router-dom";

const Scrolltotop = () => {
  const { pathname } = useLocation();

  useEffect(() => {
    setTimeout(() => {
      window.scrollTo(0, 0);
    }); // Adjust the timeout as necessary
  }, [pathname]);

  return null; // No need to render anything
};

export default Scrolltotop;
